-- ----------------------------
-- Table structure for aboutus
-- ----------------------------
DROP TABLE IF EXISTS `aboutus`;
CREATE TABLE `aboutus`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '名称',
  `content` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '内容',
  `sort` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '类别',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 34 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of aboutus
-- ----------------------------
INSERT INTO `aboutus` VALUES (31, '公司简介', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; overflow-wrap: break-word; color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; &nbsp;江西鑫磊光电科技有限公司，是显示屏、电子元器件、单元板（江西LED显示屏，南昌LED显示屏，南昌显示屏，南昌电子显示屏，南昌LED显示屏租赁，联系电话：15579511751周经理）等产品专业生产加工专业生产LED户外亮化产品，为LED工程商，广告设计商，照明行业工程公司提供优势产品，主要系列有：像素灯，点光源，超级轮廓灯，护栏管，洗墙灯，视频灯条，投光灯及大型LED灯光显示屏。是一家专业从事LED产品应用研发、设计、生产、销售和服务于一体的产品服 务商，同时也是目前国内最大的LED产品应用系统解决方案服务商之一。&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; overflow-wrap: break-word; color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; &nbsp;公司自成立以来，依托华中科技大学和清华大学的技术平台背景，建设有光电研究设计院和多个产学研转换基地。基于对LED应用产业的前瞻性研究及经营战略的精确定位，鑫磊光电科技实现了产品特色化、市场国际化、业务全面化、服务专业化的发展格局；以其独特的竞争优势，迅速发展成为国内LED应用产品领域的领军企业。公司业务涵盖LED室内高清拼接墙应用解决方案、体育场馆LED应用解决方案、租赁市场LED显示应用解决方案、城市综合体LED显示应用解决方案、LED照明应用解决方案和工程服务等多元环节。公司销售网络遍布国内外，产品远销亚、欧、澳、美、非等六大洲；工程案例遍布全国各地，并在香港、澳门、南亚、非洲、美洲成功实施了多个具有代表性的项目工程。追求卓越，追求超越，秉承“诚信、双赢”的合作宗旨，鑫磊光电科技为“最具竞争力的光电产业服务商”的持续发展和提升在不懈努力</p>', '7');
INSERT INTO `aboutus` VALUES (30, '人才招聘', '<p><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">LED显示屏钢结构工程师</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">任职资格：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　1、工民建或机械等相关专业，两年以上设计相关经验;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　2、熟悉钢结构设计和土建图纸，精通CAD绘图软件，熟悉PKPM或3D3S者优先;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　3、熟练掌握钢结构设计基本原理和方法，能做基本力学分析及计算;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　4、了解LED显示屏产品结构、特性、技术参数，有LED显示屏钢结构设计经验优先考虑;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　5、具备良好的沟通协调能力，有较强的服务意识，能适应出差。</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　工作职责：</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　1、LED显示屏安装现场勘查;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　2、负责制定钢结构的设计方案及选型评估，钢结构载荷计算;</span><br/><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　3、LED显示屏工程技术支持。</span></p>', '28');
INSERT INTO `aboutus` VALUES (29, '联系我们', '<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; background-color: rgb(255, 255, 255); color: rgb(255, 0, 0); font-size: 36px;\">　　暂没有信息</span></p>', '29');
INSERT INTO `aboutus` VALUES (32, '企业文化', '<p><span style=\"color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">　　团队精神, 敬畏客户, 勇于负责, 持续成长</span></p>', '9');
INSERT INTO `aboutus` VALUES (33, '服务项目', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; overflow-wrap: break-word; color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"text-decoration:underline;\"><strong>专业生产批发LED显示屏，另外承接各种大小工程</strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; overflow-wrap: break-word; color: rgb(102, 102, 102); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><span style=\"text-decoration:underline;\">LED显示屏制作LED显示屏维修LED显示屏安装服务 师傅活儿快，质量保证，值得您信赖</span></strong><strong><span style=\"text-decoration:underline;\"><br/>江西鑫磊光电led生产基地是一家专业从事LED应用产品研发、设计、生产、销售和服务的高新技术企业，致力于为客户提供高效、节能、可靠的LED应用产品及其整体解决方案。</span></strong></p><p><br/></p>', '8');

